import React, { useEffect } from 'react'

function Hero() {


    useEffect(()=> {
        // Change The Hero Highlighted Text Change To Read And Write In Every Inerval
        let hero_highlight_text = document.getElementById('hero-highlight-text');
        let count = 0;

    setInterval(function() {
      count++;
      hero_highlight_text.innerHTML = count % 2 === 0 ? "Read" : "Write";
      hero_highlight_text.classList.replace(`${count % 2 === 0 ? "text-green-500" : 'text-blue-500'}`,`${count % 2 === 0 ? "text-blue-500" : 'text-green-500'}`);
    }, 5000);
    },[])


  return (
    <div className='w-full h-[600px] z-10 mt-20 flex' >
       <div className="w-1/2 h-full  flex pl-24 flex-col pt-20">
       <h1 className='font-black text-[80px] leading-[1.2]' >Stay Inspire. <br /> <span id='hero-highlight-text' className='text-blue-500' >Read</span> With AI</h1>
        <p className='font-semibold text-lg mt-6' >Inspire is an AI powered blog website. It helps you create <br /> and publish creative and unique blogs automatically using AI.</p>

        <button className='py-3 w-60 px-6 mt-8 bg-slate-900 hover:bg-slate-800 transition-colors text-white mr-24 font-medium rounded-full' >Start reading</button>
       </div>

       <div className="w-1/2 h-full  flex flex-col justify-center pl-12">
        <div className="h-32 w-96 bg-slate-50 my-3 rounded-lg flex flex-col justify-center cursor-pointer hover:border-slate-800 hover:border">
            <div className="flex">
                <img src="https://miro.medium.com/fit/c/40/40/1*AwBz4NW9_M45J-sBlmH-lA.png" alt="obama"  />
            </div>
        </div>
       </div>

    </div>
  )
}

export default Hero
