import { useDisclosure } from "@chakra-ui/react";
import React, { useState } from "react";
import AuthModal from "../MiniComponents/AuthModal";


function Header() {

  const { isOpen, onOpen, onClose } = useDisclosure()
  let [isLogin,setIsLogin] = useState()

  return (
    <div className="w-full h-20 z-50 bg-white flex fixed top-0 items-center border-b border-b-slate-800">
      <AuthModal onClose={onClose} isOpen={isOpen} />
      <div className="w-1/2 h-full flex items-center">
        <h1 className="text-3xl font-bold ml-28">Inspire.</h1>
      </div>
      <div className="w-1/2 h-full flex items-center justify-end">
        <ul className="flex">
          <li className="font-semibold mx-3 cursor-pointer hover:text-indigo-500 transition-colors">
            Membership
          </li>
          <li className="font-semibold mx-3 cursor-pointer hover:text-indigo-500 transition-colors">
            Write
          </li>
          <li className="font-semibold mx-3 mr-6 cursor-pointer hover:text-indigo-500 transition-colors">
            Sign in
          </li>
        </ul>

        <button onClick={onOpen} className="py-2 px-6 bg-slate-900 hover:bg-slate-800 transition-colors text-white mr-28 font-medium rounded-full">
          Get started
        </button>
      </div>
    </div>
  );
}

export default Header;
