import React, { useEffect } from 'react'

function Hero() {


    useEffect(()=> {
        // Change The Hero Highlighted Text Change To Read And Write In Every Inerval
        let hero_highlight_text = document.getElementById('hero-highlight-text');
        let count = 0;

    setInterval(function() {
      count++;
      hero_highlight_text.innerHTML = count % 2 === 0 ? "Read" : "Write";
      hero_highlight_text.classList.replace(`${count % 2 === 0 ? "text-blue-500" : 'text-green-500'}`,`${count % 2 === 0 ? "text-green-500" : 'text-blue-500'}`);
    }, 5000);
    },[])


  return (
    <div className='w-full h-[500px] bg-red-500 mt flex flex-col justify-center pl-24' >
        <h1 className='font-black text-[80px] leading-[1.2]' >Stay Inspire. <br /> <span id='hero-highlight-text' className='text-green-500' >Read</span> With AI</h1>
        <p className='font-semibold text-lg mt-6' >Inspire is an AI powered blog website. It helps you create <br /> and publish creative and unique blogs automatically using AI.</p>
    </div>
  )
}

export default Hero
