function Hero() {


    useEffect(()=> {
        // Change The Hero Highlighted Text Change To Read And Write In Every Inerval
        let hero_highlight_text = document.getElementById('hero-highlight-text');
        let count = 0;

    setInterval(function() {
      count++;
      hero_highlight_text.innerHTML = count % 2 === 0 ? "Read" : "Write";
      hero_highlight_text.classList.replace(`${count % 2 === 0 ? "Read" : 'text-green-'}`);
    }, 5000);
    },[])


  return (
    <div className='w-full h-[500px] flex items-center pl-24' >
        <h1 className='font-black text-[80px]' >Stay Inspire. <br /> <span id='hero-highlight-text' className='text-green-500' >Read</span> With AI</h1>
    </div>
  )
}

export default Hero
