import React from 'react'

function TOAST() {
    const toast = useToast()
  return (

    toast({
        position: 'bottom',
        render: () => (
          <div className='py-3 px-6 flex justify-center items-center bg-black text-white rounded-full mb-8' >
            Saved to lists
          </div>
        ),
      })

  )
}

export default TOAST
